                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ;
s = (m+1)^2;
n0 = zeros(s);
r = 0.5;
m=m+1;

for n=m:-1:1

for i=0:n-1
    for j=0:n-1
        for k=0:n-1
            for l=0:n-1
                if ((i+j)==n-1) && ((k+l)==n-1)
                    n0(m*i+j+1,m*k+l+1) = (1/factorial(n-1))*nchoosek(i+j,i)*nchoosek(k+l,k)*(sqrt(1-r)^j)*(sqrt(r)^i)*(sqrt(1-r)^l)*(sqrt(r)^k);
                end
            end 
        end
    end
end

end

disp(n0)
disp(n0*n0)