                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ;
s = (m+1)^2;
n0 = zeros(s,1);
n1 = zeros(s,1);
% r = 0.5;

for r = y
    for i=0:m
        for j=0:m
             if ((i+j) <= m)
                ii = 1;
                jj = 1;
                if(i>0); ii = sqrt(i); end
                if(j>0); jj = sqrt(j); end
                n0((m+1)*i+j+1,1) = (1/sqrt(factorial(i+j)))*ii*jj*nchoosek(i+j,i)*(sqrt(1-r)^j)*(sqrt(r)^i);
            end
        end 
    end 
    
    norm = 0;
    for i=1:length(n0)
        norm = norm + n0(i)^2;
    end
    n0 = (1/sqrt(norm))*n0;
    
    phi00 = n0*n0';
    
    for i=0:m
        for j=0:m
             if ((i+j) <= m)
                ii = 1;
                jj = 1;
                if(i>0); ii = sqrt(i); end
                if(j>0); jj = sqrt(j); end
                n1((m+1)*i+j+1,1) = (1/sqrt(factorial(i+j)))*ii*jj*nchoosek(i+j,i)*(sqrt(1-r)^i)*(sqrt(r)^j);
            end
        end 
    end 
    
    norm = 0;
    for i=1:length(n1)
        norm = norm + n1(i)^2;
    end
    n1 = (1/sqrt(norm))*n1;

    phi11 = n1*n1';
    operator = (1/2) * (phi00 + phi11);
    
    % the SDP
        cvx_begin sdp quiet
            variable X(s,s) hermitian semidefinite
            maximize (trace(operator*X))
            trace(X) == 1
            X >= 0
        
        cvx_end
    
        x(end + 1) = cvx_optval;

end

% now for finding the total cheating probability
P_A = [];
for i = x
    P_A(end+1) = (1/2)*i + (1/2);
end


%previously known max function
x = 0:10;
f = [];

for i=x
   f(i+1) = (3/4) + (1/2)*(sqrt((i/10)*(1-(i/10))));
end


%disp(cvx_optval)

disp(P_A)
plot(y,P_A,y,f),xlabel('r'),ylabel('max probability')
